local a={Colors={["CYAN"]="85, 255, 255",["BLUE"]="0, 150, 255",["RED"]="255, 85, 85",["YELLOW"]="255, 255, 85",["GREEN"]="85, 255, 85",["MAGENTA"]="255, 85, 255",["WHITE"]="255, 255, 255"},Services={RunService=game:GetService("RunService"),CoreGui=game:GetService("CoreGui")}}a.ChangeColor=function()local b;repeat task.wait()b=a.Services.CoreGui:FindFirstChild("DevConsoleMaster")until b;local c;c=a.Services.RunService.Heartbeat:Connect(function()local d,e=pcall(function()for f,g in pairs(b:GetDescendants())do if g:IsA("TextLabel")then g.RichText=true end end end)if not d then warn(string.format("LoggerLibrary error: %s",e))c:Disconnect()task.wait(2)a.ChangeColor()end end)end;a.Print=function(h,i,j)i=i or"INFO"local k=j or a.Colors["WHITE"]local l=""if i=="DEBUG"then k=a.Colors["BLUE"]l="[HC-DEBUG]"elseif i=="INFO"then k=a.Colors["CYAN"]l="[HC-INFO]"elseif i=="ERROR"then k=a.Colors["RED"]l="[HC-ERROR]"elseif i=="SUCCESS"then k=a.Colors["GREEN"]l="[HC-SUCCESS]"elseif i=="WARNING"then k=a.Colors["YELLOW"]l="[HC-WARNING]"else l=string.format("[HC-%s]",tostring(i):upper())end;local m=string.format("<font color='rgb(%s)' size='15'>%s </font>%s",k,l,tostring(h))print(m)end;a.Debug=function(h)a.Print(h,"DEBUG")end;a.Info=function(h)a.Print(h,"INFO")end;a.Error=function(h)a.Print(h,"ERROR")end;a.Success=function(h)a.Print(h,"SUCCESS")end;a.Warning=function(h)a.Print(h,"WARNING")end;a.AdvancedLoadingBar=function(n,o)local p=n..tostring(math.random(500,20000))print(p)local q=nil;repeat task.wait()for f,g in pairs(a.Services.CoreGui:FindFirstChild("DevConsoleMaster"):GetDescendants())do if g:IsA("TextLabel")and string.find(g.Text:lower(),p:lower())then q=g;break end end until q;local r=os.time()local s=0;for f,t in ipairs(o)do s=s+t.steps end;local u=0;for f,t in ipairs(o)do local v=""local w=a.Colors[t.color]or a.Colors["CYAN"]for x=1,t.steps do u=u+1;v=v..(t.symbol or"#")local y=math.floor(u/s*100)q.Text=string.format("<font color='rgb(%s)' size='15'>[%s] %s [%d%%] %s</font>"